import React, { useEffect, useState } from 'react';

const MemberCircle = ({ groupData, hidden, animationsLoaded }) => {
  const [rotation, setRotation] = useState(0);

  // Add rotation animation effect
  useEffect(() => {
    // Only start rotation after initial animations are complete
    if (!animationsLoaded) return;

    const rotationInterval = setInterval(() => {
      setRotation(prevRotation => (prevRotation + 0.1) % 360);
    }, 50);

    return () => clearInterval(rotationInterval);
  }, [animationsLoaded]);

  return (
    <div className={`circle-wrapper ${animationsLoaded ? 'animate-circle' : ''}`}>
      <div className="dotted-circle"></div>
      <div className="center-info">
        <h2 id="total-pool" className={`${animationsLoaded ? 'animate-pool' : ''}`}>
          {hidden ? '****' : groupData.poolSize}
        </h2>
        <p className={`payment-info ${animationsLoaded ? 'animate-payment-info' : ''}`}>
          Next Payment: $100<br/>Due: June 24, 2025
        </p>
      </div>

      {/* Render members in a circle with rotating animation */}
      <div className="members-container" style={{ transform: `rotate(${rotation}deg)` }}>
        {groupData.members.map((initials, index) => {
          const angle = (2 * Math.PI / groupData.members.length) * index;
          const radius = 150;
          const x = radius * Math.cos(angle);
          const y = radius * Math.sin(angle);

          return (
            <div 
              key={index}
              className={`member ${animationsLoaded ? 'animate-member' : ''}`}
              style={{
                transform: `translate(${x}px, ${y}px) rotate(${-rotation}deg)`, // Counter-rotate to keep text upright
                animationDelay: `${0.2 + (index * 0.15)}s`
              }}
            >
              {initials}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MemberCircle;
