import React, { useState } from 'react';

const MemberCircle = ({ groupData, hidden, animationsLoaded }) => {
  const [hoveredMember, setHoveredMember] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  const handleMouseEnter = (memberData, event) => {
    setHoveredMember(memberData);
    setMousePosition({ x: event.clientX, y: event.clientY });
  };

  const handleMouseMove = (event) => {
    if (hoveredMember) {
      setMousePosition({ x: event.clientX, y: event.clientY });
    }
  };

  const handleMouseLeave = () => {
    setHoveredMember(null);
  };

  return (
    <div className={`circle-wrapper ${animationsLoaded ? 'animate-circle' : ''}`}>
      <div className="dotted-circle"></div>
      <div className="center-info">
        <h2 id="total-pool" className={`${animationsLoaded ? 'animate-pool' : ''}`}>
          {hidden ? '****' : groupData.poolSize}
        </h2>
        <p className={`payment-info ${animationsLoaded ? 'animate-payment-info' : ''}`}>
          Next Payment: $100<br/>Due: June 24, 2025
        </p>
      </div>

      {/* Add members-container div that will rotate */}
      <div className="members-container">
        {/* Render members in a circle */}
        {groupData.members.map((member, index) => {
          const angle = (2 * Math.PI / groupData.members.length) * index;
          const radius = 150;
          const x = radius * Math.cos(angle) + 200;
          const y = radius * Math.sin(angle) + 200;

          return (
            <div 
              key={index}
              className={`member ${animationsLoaded ? 'animate-member' : ''}`}
              style={{
                left: `${x}px`,
                top: `${y}px`,
                animationDelay: `${0.2 + (index * 0.15)}s`,
                // Counter-rotate to keep members upright while they orbit
                transform: 'translate(-50%, -50%) rotate(0deg)',
                animation: `${animationsLoaded ? 'fadeIn 0.5s ease-out forwards, ' : ''}counterRotate 30s linear infinite`
              }}
              onMouseEnter={(e) => handleMouseEnter(member, e)}
              onMouseMove={handleMouseMove}
              onMouseLeave={handleMouseLeave}
            >
              {typeof member === 'string' ? member : member.initials}
            </div>
          );
        })}
      </div>

      {/* Hover Popup */}
      {hoveredMember && (
        <div 
          className="member-popup"
          style={{
            left: `${mousePosition.x + 15}px`,
            top: `${mousePosition.y - 10}px`
          }}
        >
          <div className="popup-content">
            <h4>{typeof hoveredMember === 'string' ? hoveredMember : hoveredMember.name}</h4>
            <div className="popup-details">
              <p><strong>Status:</strong> {typeof hoveredMember === 'string' ? 'Active' : hoveredMember.status}</p>
              <p><strong>Contribution:</strong> {typeof hoveredMember === 'string' ? '$100' : hoveredMember.contribution}</p>
              <p><strong>Position:</strong> {typeof hoveredMember === 'string' ? 'Member' : hoveredMember.position}</p>
              {typeof hoveredMember === 'object' && hoveredMember.nextPayout && (
                <p><strong>Next Payout:</strong> {hoveredMember.nextPayout}</p>
              )}
            </div>
          </div>
          <div className="popup-arrow"></div>
        </div>
      )}

      <style jsx>{`
        .circle-wrapper {
          position: relative;
          width: 400px;
          height: 400px;
          margin: 0 auto 50px auto;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .dotted-circle {
          position: absolute;
          width: 400px;
          height: 400px;
          border: 2px dashed #48cae4;
          border-radius: 50%;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
        }

        .center-info {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          text-align: center;
          pointer-events: none;
          z-index: 2;
        }

        .center-info h2 {
          margin: 0;
          color: #0077b6;
          font-size: 1.8rem;
          font-weight: 700;
        }

        .center-info p {
          margin: 5px 0 0 0;
          font-size: 0.9rem;
          color: #555;
          font-weight: 300;
        }

        /* Member container that will rotate */
        .members-container {
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
          left: 0;
          animation: rotateMembers 30s linear infinite;
          transform-origin: center;
        }

        .member {
          position: absolute;
          width: 75px;
          height: 75px;
          background: linear-gradient(135deg, #0077b6, #48cae4);
          color: white;
          border-radius: 50%;
          display: flex;
          justify-content: center;
          align-items: center;
          font-weight: bold;
          font-size: 1.1rem;
          transition: transform 0.3s ease;
          box-shadow: 0 4px 10px rgba(0, 119, 182, 0.2);
          cursor: pointer;
        }

        .member:hover {
          transform: translate(-50%, -50%) scale(1.1) !important;
        }

        .animate-circle {
          animation: fadeIn 0.8s ease-out forwards;
        }

        /* Animation for members rotating around the circle */
        @keyframes rotateMembers {
          from {
            transform: rotate(0deg);
          }
          to {
            transform: rotate(360deg);
          }
        }

        /* Counter-rotation to keep members upright */
        @keyframes counterRotate {
          from {
            transform: translate(-50%, -50%) rotate(0deg);
          }
          to {
            transform: translate(-50%, -50%) rotate(-360deg);
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        .animate-member {
          animation: fadeIn 0.5s ease-out forwards;
        }

        /* Popup Styles */
        .member-popup {
          position: fixed;
          z-index: 1000;
          pointer-events: none;
          animation: popupFadeIn 0.2s ease-out;
        }

        .popup-content {
          background: white;
          border-radius: 12px;
          padding: 15px;
          box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
          border: 1px solid rgba(0, 119, 182, 0.1);
          min-width: 200px;
          position: relative;
        }

        .popup-content h4 {
          margin: 0 0 10px 0;
          color: #0077b6;
          font-size: 1.1rem;
          font-weight: 600;
        }

        .popup-details p {
          margin: 5px 0;
          font-size: 0.9rem;
          color: #333;
        }

        .popup-details strong {
          color: #0077b6;
        }

        .popup-arrow {
          position: absolute;
          top: 15px;
          left: -8px;
          width: 0;
          height: 0;
          border-top: 8px solid transparent;
          border-bottom: 8px solid transparent;
          border-right: 8px solid white;
        }

        .popup-arrow::before {
          content: '';
          position: absolute;
          top: -8px;
          left: 1px;
          width: 0;
          height: 0;
          border-top: 8px solid transparent;
          border-bottom: 8px solid transparent;
          border-right: 8px solid rgba(0, 119, 182, 0.1);
        }

        @keyframes popupFadeIn {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

// Demo component to test the functionality
const Demo = () => {
  const groupData = {
    poolSize: '$5,000',
    members: [
      { 
        initials: 'AB', 
        name: 'Alice Brown', 
        status: 'Active', 
        contribution: '$100', 
        position: 'Organizer',
        nextPayout: 'March 2025'
      },
      { 
        initials: 'CD', 
        name: 'Charlie Davis', 
        status: 'Active', 
        contribution: '$100', 
        position: 'Member',
        nextPayout: 'April 2025'
      },
      { 
        initials: 'EF', 
        name: 'Emma Foster', 
        status: 'Pending', 
        contribution: '$100', 
        position: 'Member',
        nextPayout: 'May 2025'
      },
      { 
        initials: 'GH', 
        name: 'George Hill', 
        status: 'Active', 
        contribution: '$100', 
        position: 'Member',
        nextPayout: 'June 2025'
      },
      { 
        initials: 'IJ', 
        name: 'Isabella Jones', 
        status: 'Active', 
        contribution: '$100', 
        position: 'Co-organizer',
        nextPayout: 'July 2025'
      },
      { 
        initials: 'KL', 
        name: 'Kevin Lee', 
        status: 'Late Payment', 
        contribution: '$100', 
        position: 'Member',
        nextPayout: 'August 2025'
      }
    ]
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#f8f9fa', minHeight: '100vh' }}>
      <MemberCircle 
        groupData={groupData} 
        hidden={false} 
        animationsLoaded={true} 
      />
    </div>
  );
};

export default Demo;
