import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';

const GroupPage = () => {
  const [hidden, setHidden] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    reason: '',
    amount: '',
    date: formatDate(new Date())
  });
  const [groupData, setGroupData] = useState({
    name: 'Roommates',
    poolSize: '$7,500',
    members: ['MB', 'AJ', 'NL', 'HC', 'HW']
  });
  const [loanRequests, setLoanRequests] = useState([
    {
      name: 'Alex Johnson',
      amount: '$500',
      reason: 'Medical Expense',
      date: 'May 1, 2025',
      status: '3/5 Approved',
      requiresAction: true
    }
  ]);

  // Toggle visibility of balance numbers
  const toggleBalances = () => {
    setHidden(!hidden);
  };

  // Open the loan request modal
  const openModal = () => {
    setModalOpen(true);
    setFormData({
      ...formData,
      date: formatDate(new Date())
    });
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
  };

  // Submit a new loan request
  const submitRequest = () => {
    const { reason, amount } = formData;

    if (reason && amount) {
      const newRequest = {
        name: 'You',
        amount: `$${amount}`,
        reason: reason,
        date: formData.date,
        status: `0/${groupData.members.length} Approved`,
        requiresAction: false
      };

      setLoanRequests([...loanRequests, newRequest]);
      setModalOpen(false);
      setFormData({
        reason: '',
        amount: '',
        date: formatDate(new Date())
      });
    }
  };

  // Format date for display
  function formatDate(dateObj) {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return dateObj.toLocaleDateString('en-US', options);
  }

  return (
    <>
      {/* Note: Header and Footer components are already implemented elsewhere */}

      <div className="container">
        <div className="group-name">
          <h2>{groupData.name}</h2>
        </div>

        <div className="circle-wrapper" id="circle">
          <div className="dotted-circle"></div>
          <div className="center-info">
            <h2 id="total-pool">{hidden ? '****' : groupData.poolSize}</h2>
            <p>Next Payment: $100<br/>Due: June 24, 2025</p>
          </div>

          {/* Render members in a circle */}
          {groupData.members.map((initials, index) => {
            const angle = (2 * Math.PI / groupData.members.length) * index;
            const radius = 150;
            const x = radius * Math.cos(angle) + 200;
            const y = radius * Math.sin(angle) + 200;

            return (
              <div 
                key={index}
                className="member" 
                style={{
                  left: `${x}px`,
                  top: `${y}px`
                }}
              >
                {initials}
              </div>
            );
          })}
        </div>

        <div className="loan-header">
          <h2>Loan Requests</h2>
          <button className="request-loan-btn" onClick={openModal}>
            Request a Loan
          </button>
        </div>

        <div className="table">
          <table id="loan-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Amount</th>
                <th>Reason</th>
                <th>Requested Date</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody id="loan-body">
              {loanRequests.map((request, index) => (
                <tr key={index}>
                  <td>{request.name}</td>
                  <td>{request.amount}</td>
                  <td>{request.reason}</td>
                  <td>{request.date}</td>
                  <td>{request.status}</td>
                  <td>
                    {request.requiresAction ? (
                      <>
                        <button className="approve-btn">Approve</button>
                        <button className="decline-btn">Decline</button>
                      </>
                    ) : (
                      "No Action Required"
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Loan Request Modal */}
      {modalOpen && (
        <div className="modal" id="loan-modal">
          <div className="modal-content">
            <input 
              type="text" 
              id="reason" 
              placeholder="Reason" 
              required 
              value={formData.reason}
              onChange={handleInputChange}
            />
            <input 
              type="number" 
              id="amount" 
              placeholder="Amount" 
              required 
              value={formData.amount}
              onChange={handleInputChange}
            />
            <input 
              type="text" 
              id="date" 
              value={formData.date} 
              readOnly 
            />
            <button className="submit-btn" onClick={submitRequest}>
              Submit Request
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default GroupPage;
