import React, { useState, useEffect, useRef, useCallback } from 'react';

const GroupPage = () => {
  // Enhanced state management
  const [hidden, setHidden] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [animateCircle, setAnimateCircle] = useState(false);
  const [animateTable, setAnimateTable] = useState(false);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sortBy, setSortBy] = useState('date');
  const [sortOrder, setSortOrder] = useState('desc');
  const [filterStatus, setFilterStatus] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMember, setSelectedMember] = useState(null);

  const [formData, setFormData] = useState({
    reason: '',
    amount: '',
    date: formatDate(new Date()),
    priority: 'normal'
  });

  const [groupData, setGroupData] = useState({
    name: 'Roommates',
    poolSize: 7500,
    totalContributed: 6200,
    members: [
      { initials: 'MB', name: 'Mike Brown', balance: 1200, contributions: 1500 },
      { initials: 'AJ', name: 'Alex Johnson', balance: -300, contributions: 1200 },
      { initials: 'NL', name: 'Nina Lopez', balance: 800, contributions: 1300 },
      { initials: 'HC', name: 'Henry Chen', balance: 600, contributions: 1100 },
      { initials: 'HW', name: 'Hannah Wilson', balance: 200, contributions: 1100 }
    ],
    nextPayment: { amount: 100, dueDate: new Date('2025-06-24') }
  });

  const [loanRequests, setLoanRequests] = useState([
    {
      id: 1,
      name: 'Alex Johnson',
      amount: 500,
      reason: 'Medical Expense',
      date: new Date('2025-05-01'),
      status: 'pending',
      approvals: 3,
      totalMembers: 5,
      requiresAction: true,
      priority: 'high'
    },
    {
      id: 2,
      name: 'Nina Lopez',
      amount: 200,
      reason: 'Car Repair',
      date: new Date('2025-04-28'),
      status: 'approved',
      approvals: 5,
      totalMembers: 5,
      requiresAction: false,
      priority: 'normal'
    }
  ]);

  // Refs
  const circleRef = useRef(null);
  const tableRef = useRef(null);
  const notificationRef = useRef(null);

  // Animation and intersection observer setup
  useEffect(() => {
    const options = { threshold: 0.2, rootMargin: '0px 0px -50px 0px' };

    const circleObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setAnimateCircle(true);
          circleObserver.unobserve(entry.target);
        }
      });
    }, options);

    const tableObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setAnimateTable(true);
          tableObserver.unobserve(entry.target);
        }
      });
    }, options);

    if (circleRef.current) circleObserver.observe(circleRef.current);
    if (tableRef.current) tableObserver.observe(tableRef.current);

    return () => {
      if (circleRef.current) circleObserver.unobserve(circleRef.current);
      if (tableRef.current) tableObserver.unobserve(tableRef.current);
