        const newStatus = action === 'decline' ? 'declined' : 
                         newApprovals >= request.totalMembers ? 'approved' : 'pending';

        return {
          ...request,
          approvals: newApprovals,
          status: newStatus,
          requiresAction: newStatus === 'pending'
