import React, { useState, useEffect, useRef } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import GridBackground from '../components/GridBackground';
import MemberCircle from '../components/MemberCircle';
import LoanRequestTable from '../components/LoanRequestTable';
import LoanRequestModal from '../components/LoanRequestModal';
import '../css/App.css';
import '../css/GroupPage.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, EffectCoverflow } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/navigation';

const GroupPage = () => {
  const [hidden, setHidden] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [animationsLoaded, setAnimationsLoaded] = useState(false);
  const [formData, setFormData] = useState({
    reason: '',
    amount: '',
    date: formatDate(new Date())
  });
  const [groupData, setGroupData] = useState({
    name: 'Roomate Rent',
    poolSize: '$7,500',
    groupBalance: '$5,000',
    members: ['MB', 'AJ', 'NL', 'HC', 'HW']
  });
  const [loanRequests, setLoanRequests] = useState([
    {
      name: 'Alex Johnson',
      amount: '$500',
      reason: 'Medical Expense',
      date: 'May 1, 2025',
      status: '3/5 Approved',
      requiresAction: true
    }
  ]);

  // Trigger animations when component mounts
  useEffect(() => {
    setTimeout(() => {
      setAnimationsLoaded(true);
    }, 100);
  }, []);

  // Toggle visibility of balance numbers
  const toggleBalances = () => {
    setHidden(!hidden);
  };

  // Open the loan request modal
  const openModal = () => {
    setModalOpen(true);
    setFormData({
      ...formData,
      date: formatDate(new Date())
    });
    document.body.classList.add('modal-open');
  };

  // Close the modal
  const closeModal = () => {
    const modalElement = document.getElementById('loan-modal');
    if (modalElement) {
      modalElement.classList.add('fade-out');

      setTimeout(() => {
        setModalOpen(false);
        document.body.classList.remove('modal-open');
        if (modalElement) {
          modalElement.classList.remove('fade-out');
        }
      }, 300);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
  };

  // Submit a new loan request
  const submitRequest = () => {
    const { reason, amount } = formData;

    if (reason && amount) {
      const newRequest = {
        name: 'You',
        amount: `$${amount}`,
        reason: reason,
        date: formData.date,
        status: `0/${groupData.members.length} Approved`,
        requiresAction: false
      };

      setLoanRequests([...loanRequests, newRequest]);
      closeModal();
      setFormData({
        reason: '',
        amount: '',
        date: formatDate(new Date())
      });
    }
  };

  // Format date for display
  function formatDate(dateObj) {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return dateObj.toLocaleDateString('en-US', options);
  }

  return (
    <>
      <GridBackground />
      <Header />

      <main className="main-content">
        <div className={`container ${animationsLoaded ? 'animate-container' : ''}`}>
          <div className={`group-name ${animationsLoaded ? 'animate-title' : ''}`}>
            <h2>{groupData.name}</h2>
          </div>

        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <MemberCircle 
                groupData={groupData} 
                hidden={hidden} 
                animationsLoaded={animationsLoaded} 
            />
            <MemberCircle 
                groupData={groupData} 
                hidden={hidden} 
                animationsLoaded={animationsLoaded} 
            />
            <MemberCircle 
                groupData={groupData} 
                hidden={hidden} 
                animationsLoaded={animationsLoaded} 
            />
        </div>
        <Swiper
    modules={[Navigation, EffectCoverflow]}
    effect="coverflow"
    grabCursor={true}
    centeredSlides={true}
    slidesPerView={3}
    loop={true}
    spaceBetween={30}
    coverflowEffect={{
        rotate: 0,
        stretch: 0,
        depth: 150,
        modifier: 1.5,
        slideShadows: false,
    }}
    navigation
    className="member-carousel"
    >
    {groupData.members.map((_, index) => (
        <SwiperSlide key={index}>
        <MemberCircle 
            groupData={groupData} 
            hidden={hidden} 
            animationsLoaded={animationsLoaded} 
        />
        </SwiperSlide>
    ))}
    </Swiper>

        <div style={{display: 'flex', flexDirection: 'row', gap: '50px'}}>
            <div>
                <div className={`loan-header ${animationsLoaded ? 'animate-loan-header' : ''}`}>
                    <h2>Loan Requests</h2>
                    <button className="request-loan-btn" onClick={openModal}>
                    +
                    </button>
                </div>
                <LoanRequestTable 
                    loanRequests={loanRequests} 
                    animationsLoaded={animationsLoaded} 
                />
            </div>

            <div>
                <div className={`loan-header ${animationsLoaded ? 'animate-loan-header' : ''}`}>
                    <h2>Loan Requests</h2>
                    <button className="request-loan-btn" onClick={openModal}>
                    +
                    </button>
                </div>
                <LoanRequestTable 
                    loanRequests={loanRequests} 
                    animationsLoaded={animationsLoaded} 
                />
            </div>
        </div>



        </div>
      </main>

      {modalOpen && (
        <LoanRequestModal
          formData={formData}
          handleInputChange={handleInputChange}
          submitRequest={submitRequest}
          closeModal={closeModal}
          handleBackdropClick={(e) => {
            if (e.target.id === 'loan-modal') {
              closeModal();
            }
          }}
        />
      )}

      <Footer />
    </>
  );
};

export default GroupPage;
