import React, { useState, useEffect } from 'react';

const CustomMemberCircle = ({ groupData, hidden, animationsLoaded }) => {
  // Function to calculate member positions around the circle
  const getMemberPosition = (index, totalMembers, radius = 180) => {
    const angle = (index * 360) / totalMembers;
    const radian = (angle * Math.PI) / 180;
    const x = Math.cos(radian) * radius;
    const y = Math.sin(radian) * radius;
    return {
      left: `calc(50% + ${x}px)`,
      top: `calc(50% + ${y}px)`
    };
  };

  return (
    <div className={`circle-wrapper ${animationsLoaded ? 'animate-circle' : ''}`}>
      <div className="dotted-circle"></div>

      {/* Rotating members container */}
      <div className={`members-container ${animationsLoaded ? 'animate-members' : ''}`}>
        {groupData.members.map((member, index) => {
          const position = getMemberPosition(index, groupData.members.length);
          return (
            <div
              key={index}
              className={`member ${animationsLoaded ? 'animate-member' : ''}`}
              style={{
                ...position,
                animationDelay: `${index * 0.1}s`,
                // Add the counter-rotate animation to keep members upright
                animation: animationsLoaded 
                  ? `fadeIn 0.5s ease-out forwards ${index * 0.1}s, counterRotate 30s linear infinite`
                  : 'counterRotate 30s linear infinite'
              }}
            >
              {member}
            </div>
          );
        })}
      </div>

      {/* Center info */}
      <div className="center-info">
        <h2>{hidden ? '••••' : groupData.groupBalance}</h2>
        <p>out of</p>
        <div style={{ marginTop: '10px' }}>
          <h2 style={{ fontSize: '2rem', color: '#48cae4' }}>
            {hidden ? '••••' : groupData.poolSize}
          </h2>
          <p style={{ fontSize: '1.2rem' }}>Available</p>
        </div>
      </div>

      <style jsx>{`
        .circle-wrapper {
          width: 400px;
          height: 100%;
          max-height: 400px;
          display: flex;
          justify-content: center;
          align-items: center;
          position: relative;
        }

        .dotted-circle {
          position: absolute;
          width: 400px;
          height: 400px;
          border: 2px dashed #48cae4;
          border-radius: 50%;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
        }

        .center-info {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          text-align: center;
          pointer-events: none;
        }

        .center-info h2 {
          margin: 0;
          color: #0077b6;
          font-size: 3rem;
          font-weight: 700;
        }

        .center-info p {
          margin: 5px 0 0 0;
          font-size: 1.5rem;
          color: #555;
          font-weight: 300;
        }

        /* Member container that will rotate */
        .members-container {
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
          left: 0;
          animation: rotateMembers 30s linear infinite;
          transform-origin: center;
        }

        .member {
          position: absolute;
          width: 75px;
          height: 75px;
          background: linear-gradient(135deg, #0077b6, #48cae4);
          color: white;
          border-radius: 50%;
          display: flex;
          justify-content: center;
          align-items: center;
          font-weight: bold;
          font-size: 1.1rem;
          transform: translate(-50%, -50%);
          transition: transform 0.3s ease;
          box-shadow: 0 4px 10px rgba(0, 119, 182, 0.2);
        }

        .member:hover {
          transform: translate(-50%, -50%) scale(1.1);
        }

        @keyframes rotateMembers {
          from {
            transform: rotate(0deg);
          }
          to {
            transform: rotate(360deg);
          }
        }

        @keyframes counterRotate {
          from {
            transform: translate(-50%, -50%) rotate(0deg);
          }
          to {
            transform: translate(-50%, -50%) rotate(-360deg);
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        .animate-circle {
          animation: fadeIn 0.8s ease-out forwards;
        }

        .animate-member {
          animation: fadeIn 0.5s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

// Demo component to show it working
const GroupPage = () => {
  const [hidden, setHidden] = useState(false);
  const [animationsLoaded, setAnimationsLoaded] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setAnimationsLoaded(true);
    }, 100);
  }, []);

  const allGroups = [
    {
      name: 'Roommate Rent',
      poolSize: '$7,500',
      groupBalance: '$5,000',
      members: ['MB', 'AJ', 'NL', 'HC', 'HW']
    },
    {
      name: 'Vacation Trip',
      poolSize: '$3,200',
      groupBalance: '$2,300',
      members: ['AD', 'BC', 'CD']
    },
    {
      name: 'Startup Fund',
      poolSize: '$10,000',
      groupBalance: '$8,000',
      members: ['AA', 'BB', 'CC', 'DD']
    }
  ];

  return (
    <div style={{ padding: '50px', background: '#f5f5f5', minHeight: '100vh' }}>
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h1 style={{ color: '#0077b6', marginBottom: '10px' }}>Fixed Custom Member Circle</h1>
        <button 
          onClick={() => setHidden(!hidden)}
          style={{
            background: '#0077b6',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '25px',
            cursor: 'pointer'
          }}
        >
          {hidden ? 'Show' : 'Hide'} Details
        </button>
      </div>

      <div style={{ display: 'flex', gap: '50px', justifyContent: 'center', flexWrap: 'wrap' }}>
        {allGroups.map((group, index) => (
          <div key={index} style={{ textAlign: 'center' }}>
            <h3 style={{ color: '#0077b6', marginBottom: '20px' }}>{group.name}</h3>
            <CustomMemberCircle 
              groupData={group}
              hidden={hidden}
              animationsLoaded={animationsLoaded}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default GroupPage;
