import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import Header from '../components/Header';
import Footer from '../components/Footer';
import GridBackground from '../components/GridBackground';

const GroupPage = () => {
  const [hidden, setHidden] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [animateCircle, setAnimateCircle] = useState(false);
  const [animateTable, setAnimateTable] = useState(false);
  const [formData, setFormData] = useState({
    reason: '',
    amount: '',
    date: formatDate(new Date())
  });
  const [groupData, setGroupData] = useState({
    name: 'Roommates',
    poolSize: '$7,500',
    members: ['MB', 'AJ', 'NL', 'HC', 'HW']
  });
  const [loanRequests, setLoanRequests] = useState([
    {
      name: 'Alex Johnson',
      amount: '$500',
      reason: 'Medical Expense',
      date: 'May 1, 2025',
      status: '3/5 Approved',
      requiresAction: true
    }
  ]);

  // References for intersection observer
  const circleRef = useRef(null);
  const tableRef = useRef(null);

  // Setup intersection observer for animations
  useEffect(() => {
    const options = {
      threshold: 0.2,
      rootMargin: '0px 0px -50px 0px'
    };

    const circleObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setAnimateCircle(true);
          circleObserver.unobserve(entry.target);
        }
      });
    }, options);

    const tableObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setAnimateTable(true);
          tableObserver.unobserve(entry.target);
        }
      });
    }, options);

    if (circleRef.current) {
      circleObserver.observe(circleRef.current);
    }
    if (tableRef.current) {
      tableObserver.observe(tableRef.current);
    }

    return () => {
      if (circleRef.current) circleObserver.unobserve(circleRef.current);
      if (tableRef.current) tableObserver.unobserve(tableRef.current);
    };
  }, []);

  // Setup ripple effect for buttons
  useEffect(() => {
    const buttons = document.querySelectorAll('.request-loan-btn, .approve-btn, .decline-btn, .submit-btn');

    const handleRipple = (e) => {
      const button = e.currentTarget;

      const circle = document.createElement('span');
      const diameter = Math.max(button.clientWidth, button.clientHeight);
      const radius = diameter / 2;

      circle.style.width = circle.style.height = `${diameter}px`;
      circle.style.left = `${e.clientX - button.getBoundingClientRect().left - radius}px`;
      circle.style.top = `${e.clientY - button.getBoundingClientRect().top - radius}px`;
      circle.classList.add('ripple');

      const ripple = button.getElementsByClassName('ripple')[0];
      if (ripple) {
        ripple.remove();
      }

      button.appendChild(circle);

      setTimeout(() => {
        circle.remove();
      }, 600);
    };

    buttons.forEach(button => {
      button.addEventListener('click', handleRipple);
    });

    return () => {
      buttons.forEach(button => {
        button.removeEventListener('click', handleRipple);
      });
    };
  }, []);

  // Toggle visibility of balance numbers
  const toggleBalances = () => {
    setHidden(!hidden);
  };

  // Open the loan request modal
  const openModal = () => {
    setModalOpen(true);
    setFormData({
      ...formData,
      date: formatDate(new Date())
    });
    document.body.classList.add('modal-open');
  };

  // Close the modal
  const closeModal = () => {
    const modalElement = document.getElementById('loan-modal');
    modalElement.classList.add('fade-out');

    setTimeout(() => {
      setModalOpen(false);
      document.body.classList.remove('modal-open');
      modalElement.classList.remove('fade-out');
    }, 300);
  };

  // Handle backdrop click to close modal
  const handleBackdropClick = (e) => {
    if (e.target.id === 'loan-modal') {
      closeModal();
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
  };

  // Submit a new loan request
  const submitRequest = () => {
    const { reason, amount } = formData;

    if (reason && amount) {
      const newRequest = {
        name: 'You',
        amount: `$${amount}`,
        reason: reason,
        date: formData.date,
        status: `0/${groupData.members.length} Approved`,
        requiresAction: false
      };

      setLoanRequests([...loanRequests, newRequest]);
      closeModal();
      setFormData({
        reason: '',
        amount: '',
        date: formatDate(new Date())
      });
    }
  };

  // Format date for display
  function formatDate(dateObj) {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return dateObj.toLocaleDateString('en-US', options);
  }

  return (
    <>
      <GridBackground />
      <Header />

      <div className="container fade-in-up">
        <div className="group-name">
          <h2 className="title-animation">{groupData.name}</h2>
        </div>

        <div 
          className={`circle-wrapper ${animateCircle ? 'animate-circle' : ''}`} 
          id="circle" 
          ref={circleRef}
        >
          <div className="dotted-circle"></div>
          <div className="center-info">
            <h2 id="total-pool" className="pool-amount-animation">
              {hidden ? '****' : groupData.poolSize}
            </h2>
            <p className="payment-info-animation">
              Next Payment: $100<br/>Due: June 24, 2025
            </p>
          </div>

          {/* Render members in a circle with staggered animation */}
          {groupData.members.map((initials, index) => {
            const angle = (2 * Math.PI / groupData.members.length) * index;
            const radius = 150;
            const x = radius * Math.cos(angle) + 200;
            const y = radius * Math.sin(angle) + 200;

            return (
              <div 
                key={index}
                className={`member member-animation`}
                style={{
                  left: `${x}px`,
                  top: `${y}px`,
                  animationDelay: `${0.2 + (index * 0.15)}s`
                }}
              >
                {initials}
              </div>
            );
          })}
        </div>

        <div className="loan-header slide-in-right">
          <h2>Loan Requests</h2>
          <button className="request-loan-btn" onClick={openModal}>
            Request a Loan
          </button>
        </div>

        <div 
          className={`table ${animateTable ? 'table-animation' : ''}`}
          ref={tableRef}
        >
          <table id="loan-table">
            <thead>
              <tr>
                <th className="th-animation" style={{ animationDelay: '0.1s' }}>Name</th>
                <th className="th-animation" style={{ animationDelay: '0.2s' }}>Amount</th>
                <th className="th-animation" style={{ animationDelay: '0.3s' }}>Reason</th>
                <th className="th-animation" style={{ animationDelay: '0.4s' }}>Requested Date</th>
                <th className="th-animation" style={{ animationDelay: '0.5s' }}>Status</th>
                <th className="th-animation" style={{ animationDelay: '0.6s' }}>Action</th>
              </tr>
            </thead>
            <tbody id="loan-body">
              {loanRequests.map((request, index) => (
                <tr 
                  key={index} 
                  className="tr-animation"
                  style={{ animationDelay: `${0.3 + (index * 0.1)}s` }}
                >
                  <td>{request.name}</td>
                  <td>{request.amount}</td>
                  <td>{request.reason}</td>
                  <td>{request.date}</td>
                  <td>{request.status}</td>
                  <td>
                    {request.requiresAction ? (
                      <div className="action-buttons">
                        <button className="approve-btn">Approve</button>
                        <button className="decline-btn">Decline</button>
                      </div>
                    ) : (
                      "No Action Required"
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Loan Request Modal */}
      {modalOpen && (
        <div className="modal" id="loan-modal" onClick={handleBackdropClick}>
          <div className="modal-content modal-animation">
            <div className="modal-header">
              <h3>Request a Loan</h3>
              <button className="close-btn" onClick={closeModal}>&times;</button>
            </div>
            <input 
              type="text" 
              id="reason" 
              placeholder="Reason" 
              required 
              value={formData.reason}
              onChange={handleInputChange}
              className="input-animation"
              style={{ animationDelay: '0.1s' }}
            />
            <input 
              type="number" 
              id="amount" 
              placeholder="Amount" 
              required 
              value={formData.amount}
              onChange={handleInputChange}
              className="input-animation"
              style={{ animationDelay: '0.2s' }}
            />
            <input 
              type="text" 
              id="date" 
              value={formData.date} 
              readOnly 
              className="input-animation"
              style={{ animationDelay: '0.3s' }}
            />
            <div className="modal-buttons">
              <button 
                className="cancel-btn" 
                onClick={closeModal}
                style={{ animationDelay: '0.4s' }}
              >
                Cancel
              </button>
              <button 
                className="submit-btn" 
                onClick={submitRequest}
                style={{ animationDelay: '0.5s' }}
              >
                Submit Request
              </button>
            </div>
          </div>
        </div>
      )}
      <Footer />
    </>
  );
};

export default GroupPage;
